<?phpclass Form_Valid_InsertUser {	public $messages;    public function __construct($data)    {      $val=new Zend_Validate_NotEmpty;	  $val2=new Zend_Validate_EmailAddress;	  $tv = new Model_Thanhvien;	  if($val->isValid($data['username'])==false)		$this->messages[]="User Name khong duoc de trong";		  if($val->isValid($data['password'])==false)		$this->messages[]="password khong duoc de trong";					  if(($data['password'])!=($data['re_password']))		$this->messages[]="password va confirm phai giong nhau";	  if($val->isValid($data['email'])==false)		$this->messages[]="email khong duoc de trong";			  if($tv->checkUser($data['username'])==false)		$this->messages[]="username da ton tai";			  if($val2->isValid($data['email'])==false)		$this->messages[]="email khong hop le";		  if($val->isValid($data['diachi'])==false)		$this->messages[]="dia chi khong duoc de trong";			}	public function valid(){	if($this->messages!="")		return false;	else		return true;	}			}?>